
SELECT COUNT(*) FROM company_name AS cn,company_type AS ct,keyword AS k,link_type AS lt,movie_companies AS mc,movie_info AS mi,movie_keyword AS mk,movie_link AS ml,title AS t WHERE t.production_year BETWEEN 2000 AND 2010 AND mc.note IS NULL AND k.keyword = 'sequel' AND cn.country_code != '[pl]' AND ct.kind = 'production companies' AND lt.link LIKE '%follow%' AND mi.info IN ('Germany', 'German') AND lt.id = ml.link_type_id AND ml.movie_id = t.id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND ml.movie_id = mi.movie_id AND t.id = mk.movie_id AND t.id = mc.movie_id AND t.id = mi.movie_id AND mk.keyword_id = k.id AND mk.movie_id = mc.movie_id AND mk.movie_id = mi.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND mc.movie_id = mi.movie_id