
SELECT COUNT(*) FROM cast_info AS ci,comp_cast_type AS cct1,complete_cast AS cc,keyword AS k,movie_info AS mi,movie_info_idx AS mi_idx,movie_keyword AS mk,title AS t WHERE t.production_year > 2000 AND cct1.kind IN ('cast', 'crew') AND k.keyword IN ('murder', 'violence', 'blood', 'gore', 'death', 'female-nudity', 'hospital') AND ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND mi.info IN ('Horror', 'Thriller') AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND t.id = cc.movie_id AND mi.movie_id = ci.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mk.movie_id AND mi.movie_id = cc.movie_id AND mi_idx.movie_id = ci.movie_id AND mi_idx.movie_id = mk.movie_id AND mi_idx.movie_id = cc.movie_id AND ci.movie_id = mk.movie_id AND ci.movie_id = cc.movie_id AND mk.movie_id = cc.movie_id AND mk.keyword_id = k.id AND cc.subject_id = cct1.id